import React from 'react';
import { TouchableOpacity, View, StyleSheet, Text, Platform, TouchableNativeFeedback} from 'react-native'

import Colors from '../constants/colors' 

import { MOUNTAINS } from '../data/dummy-data'


const HikeListTile = props => {
    let TouchableCmp =  TouchableOpacity;

    if (Platform.OS === 'android' && Platform.Version >= 21 ){
      TouchableCmp = TouchableNativeFeedback;
    }

    const titleMountain = MOUNTAINS.find(mountain => mountain.id === props.hike.mountain)

    console.log(titleMountain)


    return(
        <View 
        style={styles.hikesItem}        
        >
            <TouchableCmp
            onPress={props.onSelect}
            >
                <View>
                    <Text style={styles.title}>{titleMountain.name}</Text>
                    <Text style={styles.description}>{props.hike.time}</Text>
                    <Text style={styles.description}>{props.hike.date}</Text>
           </View>
            </TouchableCmp>
        </View>

    )

}

const styles = StyleSheet.create({
    hikesItem: {
        flex: 1,
        marginLeft: 15,
        marginRight: 15,
        marginTop:7,
        marginBottom: 7,
        padding: 15,
        height: 110,
        backgroundColor: Colors.backgroundGreen,
        borderRadius: 5,
        overflow: 'hidden'
    },
    title: {
        fontSize: 30,
        fontFamily: 'robotoSlab',
        color: Colors.primaryDarkBlue
    },
    description : {
        fontSize: 18,
        fontFamily: 'openSans',
        color: Colors.secondaryBlue

    }
})

export default HikeListTile
