import React from 'react';
import { View, Text, StyleSheet, Image } from 'react-native'


const HikeHeader = props => {
    return (
            <View style={styles.imageContainer} >
                <Image
                    style={styles.mtnImg}
                    source={{
                    uri: props.selectedMountain.mtnImg,
                    }}
                />
                <Text style={styles.headerTitle}>{props.selectedMountain.name}</Text>
            </View>
    )
};

const styles = StyleSheet.create({
    imageContainer: {
        paddingTop: 5,
        resizeMode: 'contain',
    },
    mtnImg: {
        height: 200
    },
    headerTitle: {
        fontSize: 40,
        fontFamily: 'robotoSlab',
        color: 'white',
        position: 'absolute', 
        top: 150,
        left: 17
    },
});

export default  HikeHeader;
