import React from 'react';
import { View, StyleSheet, FlatList } from 'react-native'
import { useAppSelector } from '../../common/hooks/hooks'

import Colors from '../../constants/colors'
import HikeListTile from '../../components/HikeListTile'


const HikeListScreen = ({ navigation }) => {

    const availableHikes = useAppSelector((state) => state.hikeState.hikesAvailable)

    const renderItem = (itemData) => {
        console.log(itemData);
        return (
            <HikeListTile hike={itemData.item} onSelect={(props) => {
                navigation.navigate("DetailHike", {
                    hikeId: itemData.hikeId
                })
            }}/>
        );
    }

    return (
                <View style={styles.screen}>
                    <FlatList 
                        data= {availableHikes}
                        renderItem={renderItem}
                    />
                </View>   
            );
};

const styles = StyleSheet.create({
    Screen: {
        flex: 1, 
        justifyContent: 'center',
        alignItems: 'center',
    },
    headerTitle: {
        fontSize: 40,
        marginLeft: 10,
        fontFamily: 'robotoSlab',
        bottom: 0,
        color: Colors.primaryDarkBlue
    }
});

export default  HikeListScreen;
