import React from 'react';
import { View, Text, StyleSheet, StatusBar, SafeAreaView, ScrollView } from 'react-native'
import { FlatList } from 'react-native-gesture-handler';
import { SectionList } from 'react-native';
import Colors from '../../constants/colors'
import { useAppSelector } from '../../common/hooks/hooks'
import  CalendarListTile from '../../components/CalendarListTile'

const CalendarScreen = (props) => {

    const userId = 0;
    const availableHikes = useAppSelector((state) => state.hikeState.hikesAvailable)
    const filteredHikesLeader = availableHikes.filter(hike => hike.hostId === userId);
    const filteredHikesJoined = availableHikes.map(hike => {
        hike.attendeesIds.filter( item => item == userId)
    });

    console.log('filteredHikesLeader', filteredHikesLeader.length);
    console.log('filteredHikesJoined', filteredHikesJoined.length);

    const calendarData = [   
        {
            title: 'Hikes you are leading',
            data: filteredHikesLeader
        },
        {
            title: 'Hikes you joined',
            data: filteredHikesJoined
        }
    ]


        return (
            <SafeAreaView style={styles.container}>
                <SectionList
                sections={calendarData}
                keyExtractor={(item, index) =>  item + index}
                renderItem={({ item }) => (
                    <CalendarListTile hike={item} />
                )}
                renderSectionHeader={({ section: { title } }) => (
                    <Text style={styles.headerTitle}>{title}</Text>
                )}
                stickySectionHeadersEnabled={false}
                />
          </SafeAreaView>
        );

    };

    const styles = StyleSheet.create({
        container: {
            flex: 1,
            paddingTop: StatusBar.currentHeight,
        },
        Screen: {
            flex: 1, 
            justifyContent: 'center',
            alignItems: 'center',
        },
        headerTitle: {
            fontSize: 18,
            marginLeft: 10,
            fontFamily: 'robotoSlabLight',
            bottom: 0,
            color: Colors.primaryDarkBlue
        }
    });

export default  CalendarScreen;
