import React from 'react';
import { View, Text, StyleSheet, Platform } from 'react-native'
//import { HIKES, MOUNTAINS } from '../data/dummy-data'
import { useSelector } from 'react-redux';


import Colors from "../../constants/colors"
//import { setAutoFocusEnabled } from 'expo/build/AR';


const DetailHikeScreen = props => {
    const availableMountain = useSelector(state => state.mountains.Mountains)
    const availableHikes = useSelector(state => state.hikes.hikesAvailble)

    const hikeId = props.navigation.getParam('hikeId');

    const selectedHike = availableHikes.find( hike => hike.id === hikeId)
    const selectMountain = availableMountain.find(mountain => mountain.id === selectedHike.mountain)

    DetailHikeScreen.navigationOptions = (navigationData) =>{
        const hikeId = navigationData.navigation.getParam('hikeId')
        const selectedHike = availableHikes.find( hike => hike.id === hikeId)

        return {
            headerTitle : selectedHike.mountain
        }
    }


    DetailHikeScreen.navigationOptions = {
        headerStyle: {
            backgroundColor: Colors.backgroundBlue
        },
        headerTintColor: Colors.primaryDarkBlue
    }


    return (
        <View style={styles.screen}>
            <Text>{selectMountain.name}</Text>
            <Text>{selectMountain.elevation}</Text>
            <Text>{selectMountain.mtnImg}</Text>
        </View>
    );
};

const styles = StyleSheet.create({
    Screen: {
        flex: 1, 
        justifyContent: 'center',
        alignItems: 'center',
    }

});

export default  DetailHikeScreen;
