import React from 'react';
import { View, Text, Pressable, StyleSheet, Image, Platform, StatusBar, SafeAreaView, ScrollView } from 'react-native'
import { TouchableOpacity } from 'react-native-gesture-handler';
import { useAppSelector } from '../../common/hooks/hooks';
import OrganizerCard from '../../components/OrganizerCard';
import Colors from '../../constants/colors';
import GeneralInfo from '../../components/GeneralInfo';
import HikeHeader from '../../components/HikeHeader';
import AttendeesCard from '../../components/AttendeesCard';
import GasSplitGas from '../../components/GasSplitCard';
import AboutCard from '../../components/AboutCard';
import PaceCard from '../../components/PaceCard';
import JoinCard from '../../components/JoinCard';


const DetailHikeScreen = ({ route, navigation }) => {
    console.log(route.params)
    const { hikeId } = route.params;
    const availableMountain = useAppSelector(state => state.mountainState.mountains);
    const availableHikes = useAppSelector(state => state.hikeState.hikesAvailable);
    const routeDetails = useAppSelector(state => state.routeState.routes);
    const users = useAppSelector(state => state.userState.users);

    const selectedHike = availableHikes.find( hike => hike.id === hikeId);
    console.log(selectedHike);
    const selectedMountain = availableMountain.find(mountain => mountain.id === selectedHike.mountainId);
    console.log(selectedMountain);
    const selectedRoute = routeDetails.find(route => route.id === selectedHike.routeId);
    const selectedHost = users.find(user => user.id === selectedHike.hostId);

    return (
        <SafeAreaView style={styles.container}>
            <ScrollView>
                <View style={styles.screen}>
                    <HikeHeader selectedMountain={selectedMountain}/>
                    <GeneralInfo selectedHike={selectedHike} selectedRoute={selectedRoute}/>
                    <OrganizerCard selectedHost={selectedHost} selectedRoute={selectedRoute}/>
                    <AttendeesCard users={users} selectedHike={selectedHike}/>
                    <GasSplitGas />
                    <AboutCard />
                    <PaceCard />
                </View>
            </ScrollView>
            <JoinCard />
        </SafeAreaView>
    )
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        paddingTop: StatusBar.currentHeight,
    },
    Screen: {
        flex: 1, 
        justifyContent: 'center',
        alignItems: 'center',
    },
});

export default  DetailHikeScreen;
