import React from 'react';
import { View, Text, StyleSheet, Button, FlatList, TouchableOpacity } from 'react-native'
import { useSelector } from 'react-redux';


//import { MOUNTAINS } from '../data/dummy-data'
import Hike from '../../models/hike';
import Colors from "../../constants/colors"
import HikeListTile from '../../components/HikeListTile';

import { HeaderButtons, Item } from 'react-navigation-header-buttons';
import HeaderButton from '../../components/HeaderButton'

const HikeListScreen = props => {

    const availableHikes = useSelector(state => state.hikes.hikesAvailble)

    const renderItem = (itemData) => {
        return (
            <HikeListTile hike={itemData.item} onSelect={() => {
                props.navigation.navigate({routeName: 'DetailHike', params: {
                    hikeId: itemData.item.id
                } })
            }}/>
        );
    }

    HikeListScreen.navigationOptions = {
        headerTitle: '',
        headerLeft: () => (<Text style={styles.headerTitle}>Find a Hike</Text>),
        headerStyle: {
            backgroundColor: Colors.backgroundBlue,
            height: 196,
        },
        headerTintColor: Colors.primaryDarkBlue,
    }

    return (
        <View style={styles.screen}>
            <FlatList 
                data= {availableHikes}
                renderItem={renderItem}
            />
        </View>
    );
};

const styles = StyleSheet.create({
    Screen: {
        flex: 1, 
        justifyContent: 'center',
        alignItems: 'center',
    },
    headerTitle: {
        fontSize: 40,
        marginLeft: 10,
        fontFamily: 'robotoSlab',
        bottom: 0,
        color: Colors.primaryDarkBlue
    }


});

export default  HikeListScreen;
